.o-button-tertiary,
.o-button-secondary,
.o-button-primary,
.o-button {
    display: inline-flex;
    position: relative;
    font-family: var(--font-secondary);
    font-size: var(--font-type-body-lg-fz);
    font-weight: var(--font-type-body-lg-bold-fw);
    line-height: var(--font-type-body-lg-lh);
    text-align: center;
    text-decoration: none;
    padding: 0.5em var(--space-4x) 0.4em;
    background-color: var(--color-primary);
    color: var(--color-white);
    border: 1px solid transparent;
    align-items: center;
    justify-content: center;
    gap: var(--space-2x);
    transition: 0.15s linear;
}
.o-button-secondary {
    background-color: transparent;
    color: var(--color-primary);
    border-color: var(--color-primary);
}
.o-button-tertiary {
    position: relative;
    max-width: max-content;
    padding: 0 0 var(--space-2x) 0;
    background: none;
    color: var(--color-black);
}
.o-button-tertiary:after {
    content: '';
    display: block;
    position: absolute;
    width: 130%;
    height: 1px;
    left: 0;
    bottom: 0;
    background-color: var(--color-black);
}
.o-button-text {
    color: var(--color-black);
    display: inline-flex;
    position: relative;
    font-size: var(--font-type-body-lg-fz);
    gap: var(--space-2x);
    line-height: var(--font-type-body-lg-lh);
}
.o-button-text-hover,
.o-button-text:focus-within,
.o-button-text:hover {
    color: var(--color-primary);
}
.o-button-center {
    margin-left: auto;
    margin-right: auto;
}

.o-button-primary:hover {
    background-color: var(--color-primary-hover);
}
.o-button-secondary:hover {
    background-color: var(--color-primary-hover);
    border-color: var(--color-primary-hover);
    color: var(--color-white);
}
.o-button-tertiary:hover {
    background-color: transparent;
}
.o-button-tertiary:hover:after {
    animation: progress 0.3s linear forwards;
}

@keyframes progress {
    0% {
        width: 0;
    }
    100% {
        width: 130%;
    }
}

.o-link-secondary,
.o-link {
    color: currentColor;
    text-transform: uppercase;
    position: relative;
}
.o-link-secondary {
    text-transform: none;
}
.o-link-disabled,
.o-link:disabled {
    color: var(--color-font-base);
    pointer-events: none;
}

.o-link::after {
    content: '';
    position: absolute;
    bottom: -5px;
    left: 0;
    width: 100%;
    height: 1px;
    background-color: currentColor;
    opacity: 1;
    transition: opacity 300ms, transform 300ms;
    transform: scale(0);
    transform-origin: center;
}
.o-link:hover::after,
.o-link:focus-within::after {
    transform: scale(1);
}

.o-content {
    width: 100%;
    max-width: var(--default-content-width);
    margin-left: auto;
    margin-right: auto;
}

.o-content-xlarge {
